import { Component } from "@angular/core";
import { Package } from "../shared/models/package.model";
import { PackageService } from "../shared/services/package/package.service";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { HttpClientModule } from "@angular/common/http";

@Component({
  selector: "app-create-package",
  templateUrl: "./create-package.component.html",
  styleUrls: ["./create-package.component.css"],
})
export class CreatePackageComponent {
  title = "Web Admin - Create Package";

  package: Package = {
    active_delivery_id: "",
    description: "",
    weight: 0,
    width: 0,
    height: 0,
    depth: 0,
    from_name: "",
    from_address: "",
    from_location: {
      type: "",
      coordinates: [],
    },
    to_name: "",
    to_address: "",
    to_location: {
      type: "",
      coordinates: [],
    },
  };

  submitted = false;

 constructor(private packageService: PackageService) {}

  ngOnInit(): void {

  }

  savePackage(): void {
    const data = {
      active_delivery_id: this.package.active_delivery_id,
      description: this.package.description,
      weight: this.package.weight,
      width: this.package.width,
      height: this.package.height,
      depth: this.package.depth,
      from_name: this.package.from_name,
      from_address: this.package.from_address,
      from_location: this.package.from_location,
      to_name: this.package.to_name,
      to_address: this.package.to_address,
      to_location: this.package.to_location,
    };

    this.packageService.createPackage(data).subscribe({
      next: (res) => {
        console.log(res);
        alert("Package Created Successfully");
        this.submitted = true;
      },
      error: (err) => console.log(err),
    });
  }

  newPackage(): void {
    this.submitted = false;
    this.package = {
      active_delivery_id: "",
      description: "",
      weight: 0,
      width: 0,
      height: 0,
      depth: 0,
      from_name: "",
      from_address: "",
      from_location: {
        type: "",
        coordinates: [],
      },
      to_name: "",
      to_address: "",
      to_location: {
        type: "",
        coordinates: [],
      },
    };
  }
}
