  }

  newPackage(): void {
    this.submitted = false;
    this.package = {
      active_delivery_id: "",
      description: "",
      weight: 0,
      width: 0,
      height: 0,
      depth: 0,
      from_name: "",
      from_address: "",
      from_location: {
        type: "",
        coordinates: [],
      },
      to_name: "",
      to_address: "",
      to_location: {
        type: "",
        coordinates: [],
      },
    };
  }
}
