import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Package } from '../../../create-package/package.model'; 

@Injectable({
  providedIn: 'root'
})
export class PackageService {
  private apiUrl = 'http://localhost:5000/api/package';

  constructor(private http: HttpClient) { }

  getAll(): Observable<Package[]> {
    return this.http.get<Package[]>(this.apiUrl);
  }

  getPackageById(package_id: string): Observable<Package> {
    return this.http.get<Package>(`${this.apiUrl}/${package_id}`);
  }

  createPackage(data: Package): Observable<Package> {
    return this.http.post<Package>(this.apiUrl, data);
  }

  updatePackage(package_id: string, data: Package): Observable<Package> {
    return this.http.put<Package>(`${this.apiUrl}/${package_id}`, data);
  }

  deletePackage(package_id: string): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${package_id}`);
  }
}
