import { Component } from '@angular/core';
import { Package } from './package.model';
import { PackageService } from '../shared/services/package/package.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';

@Component({
  selector: 'app-create-package',
  templateUrl: './create-package.component.html',
  styleUrls: ['./create-package.component.css']
})
export class CreatePackageComponent {
  title = 'Web Admin - Create Package';

  active_delivery_id = "";
  description = "";
  weight = 0;
  width = 0;
  height = 0;
  depth = 0;
  from_name = "";
  from_address = "";
  from_location = {
    type: "",
    coordinates: []
  };
  to_name = "";
  to_address = "";
  to_location = {
    type: "",
    coordinates: []
  };

  constructor(private packageService: PackageService) { }

  ngOnInit(): void {
    // this.packageForm = this.formBuilder.group({
    //   description: ['', Validators.required],
    //   weight: ['', Validators.required],
    //   width: ['', Validators.required],
    //   height: ['', Validators.required],
    //   depth: ['', Validators.required],
    //   from_name: ['', Validators.required],
    //   from_address: ['', Validators.required],
    //   to_name: ['', Validators.required],
    //   to_address: ['', Validators.required],
    // });
  }

  newPackage(){

    let bodyData = {
      "active_delivery_id": this.active_delivery_id,
      "description": this.description,
      "weight": this.weight,
      "width": this.width,
      "height": this.height,
      "depth": this.depth,
      "from_name": this.from_name,
      "from_address": this.from_address,
      'from_location': this.from_location,
      'to_name': this.to_name,
      'to_address': this.to_address,
      'to_location': this.to_location,
    };

    this.packageService.createPackage(bodyData).subscribe(
      (resultData: Package) => {
        console.log(resultData);
        alert("Package Created Successfully")
      },
      (error: Error) => {

      }
    );
  }

  onSubmit() {
      this.newPackage();
    }

}
